import React from 'react';

const AdCard = ({ ad, onView, onDelete }) => {
  const getImageUrl = (imagePath) => {
    return `http://localhost:5000/${imagePath}`;
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString();
  };

  return (
    <div className="ad-card" onClick={onView}>
      <img 
        src={getImageUrl(ad.imagePath)} 
        alt={ad.title}
        className="ad-image"
        onError={(e) => {
          e.target.src = 'data:image/svg+xml,%3Csvg width="300" height="200" xmlns="http://www.w3.org/2000/svg"%3E%3Crect width="100%25" height="100%25" fill="%23e0e0e0"/%3E%3Ctext x="50%25" y="50%25" font-family="Arial, sans-serif" font-size="16" fill="%23333" text-anchor="middle" dy=".3em"%3EImage not found%3C/text%3E%3C/svg%3E';
        }}
      />
      
      <div className="ad-info">
        <h3 className="ad-title">{ad.title}</h3>
        {ad.description && (
          <p className="ad-description">
            {ad.description.length > 100 
              ? ad.description.substring(0, 100) + '...' 
              : ad.description}
          </p>
        )}
        
        <div className="ad-meta">
          <span className="ad-category">{ad.category?.name}</span>
          <span className="ad-date">{formatDate(ad.createdAt)}</span>
        </div>
        
        <div className="ad-actions" onClick={(e) => e.stopPropagation()}>
          <button className="btn btn-primary" onClick={onView}>
            View
          </button>
          <button className="btn btn-danger" onClick={onDelete}>
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdCard;
