import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import AdCard from './components/AdCard';
import AdModal from './components/AdModal';
import UploadForm from './components/UploadForm';
import { getAds, getCategories, deleteAd } from './services/api';
import './App.css';

function App() {
  const [ads, setAds] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedAd, setSelectedAd] = useState(null);
  const [showUploadForm, setShowUploadForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    loadAds();
  }, [selectedCategory, searchTerm]);

  const loadData = async () => {
    try {
      setLoading(true);
      await Promise.all([loadCategories(), loadAds()]);
    } catch (error) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const loadCategories = async () => {
    try {
      const data = await getCategories();
      setCategories(data);
    } catch (error) {
      console.error('Error loading categories:', error);
    }
  };

  const loadAds = async () => {
    try {
      const params = {};
      if (selectedCategory) params.category = selectedCategory;
      if (searchTerm) params.search = searchTerm;
      
      const data = await getAds(params);
      setAds(data);
    } catch (error) {
      console.error('Error loading ads:', error);
    }
  };

  const handleDeleteAd = async (adId) => {
    if (window.confirm('Are you sure you want to delete this ad?')) {
      try {
        await deleteAd(adId);
        setAds(ads.filter(ad => ad._id !== adId));
        setSelectedAd(null);
        await loadCategories(); // Refresh category counts
      } catch (error) {
        alert('Error deleting ad');
      }
    }
  };

  const handleUploadSuccess = () => {
    loadData();
    setShowUploadForm(false);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="app">
      <Sidebar 
        categories={categories}
        selectedCategory={selectedCategory}
        onCategorySelect={setSelectedCategory}
        onNewUpload={() => setShowUploadForm(true)}
      />
      
      <main className="main-content">
        <header className="header">
          <h1>Paper Ads Manager</h1>
          <div className="search-container">
            <input
              type="text"
              placeholder="Search ads..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>
        </header>

        {error && <div className="error-message">{error}</div>}

        <div className="ads-grid">
          {ads.length > 0 ? (
            ads.map(ad => (
              <AdCard
                key={ad._id}
                ad={ad}
                onView={() => setSelectedAd(ad)}
                onDelete={() => handleDeleteAd(ad._id)}
              />
            ))
          ) : (
            <div className="empty-state">
              <h3>No ads found</h3>
              <p>
                {selectedCategory 
                  ? 'No ads in this category yet.' 
                  : 'No ads uploaded yet.'}
              </p>
              <button 
                className="btn btn-primary"
                onClick={() => setShowUploadForm(true)}
              >
                Upload First Ad
              </button>
            </div>
          )}
        </div>
      </main>

      {selectedAd && (
        <AdModal
          ad={selectedAd}
          onClose={() => setSelectedAd(null)}
          onDelete={() => handleDeleteAd(selectedAd._id)}
        />
      )}

      {showUploadForm && (
        <UploadForm
          categories={categories}
          onClose={() => setShowUploadForm(false)}
          onSuccess={handleUploadSuccess}
        />
      )}
    </div>
  );
}

export default App;
