import React from 'react';

const Sidebar = ({ categories, selectedCategory, onCategorySelect, onNewUpload }) => {
  return (
    <div className="sidebar">
      <h2>Categories</h2>
      
      <button className="upload-btn" onClick={onNewUpload}>
        + Upload New Ad
      </button>
      
      <ul className="categories">
        <li className="category-item">
          <button
            className={`category-btn ${selectedCategory === '' ? 'active' : ''}`}
            onClick={() => onCategorySelect('')}
          >
            <span>All Categories</span>
            <span className="category-count">
              {categories.reduce((total, cat) => total + cat.adCount, 0)}
            </span>
          </button>
        </li>
        
        {categories.map(category => (
          <li key={category._id} className="category-item">
            <button
              className={`category-btn ${selectedCategory === category._id ? 'active' : ''}`}
              onClick={() => onCategorySelect(category._id)}
            >
              <span>{category.name}</span>
              <span className="category-count">{category.adCount}</span>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
