import React from 'react';

const AdModal = ({ ad, onClose, onDelete }) => {
  if (!ad) return null;

  const getImageUrl = (imagePath) => {
    return `http://localhost:5000/${imagePath}`;
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString();
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>{ad.title}</h2>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>
        
        <div className="modal-body">
          <img 
            src={getImageUrl(ad.imagePath)} 
            alt={ad.title}
            className="modal-image"
          />
          
          <div style={{ marginTop: '20px' }}>
            {ad.description && (
              <div style={{ marginBottom: '15px' }}>
                <strong>Description:</strong>
                <p>{ad.description}</p>
              </div>
            )}
            
            <div style={{ marginBottom: '10px' }}>
              <strong>Category:</strong> {ad.category?.name}
            </div>
            
            <div style={{ marginBottom: '10px' }}>
              <strong>Upload Date:</strong> {formatDate(ad.createdAt)}
            </div>
            
            <div style={{ marginBottom: '10px' }}>
              <strong>File Size:</strong> {formatFileSize(ad.fileSize)}
            </div>
            
            <div style={{ marginBottom: '10px' }}>
              <strong>File Type:</strong> {ad.mimeType}
            </div>
            
            <div style={{ marginTop: '20px' }}>
              <button className="btn btn-danger" onClick={onDelete}>
                Delete Ad
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdModal;
