import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import AdCard from './components/AdCard';
import AdModal from './components/AdModal';
import UploadForm from './components/UploadForm';
import CategoryCard from './components/CategoryCard';
import { getAds, getCategories, deleteAd, createCategory, deleteCategory } from './services/api';
import './App.css';

function App() {
  const [ads, setAds] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedAd, setSelectedAd] = useState(null);
  const [showUploadForm, setShowUploadForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [viewMode, setViewMode] = useState('categories'); // 'categories' or 'ads'

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    if (selectedCategory) {
      setViewMode('ads');
      loadAds();
    } else {
      setViewMode('categories');
    }
  }, [selectedCategory, searchTerm]);

  const loadData = async () => {
    try {
      setLoading(true);
      await Promise.all([loadCategories(), loadAds()]);
    } catch (error) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const loadCategories = async () => {
    try {
      const data = await getCategories();
      setCategories(data);
    } catch (error) {
      console.error('Error loading categories:', error);
    }
  };

  const loadAds = async () => {
    try {
      const params = {};
      if (selectedCategory) params.category = selectedCategory;
      if (searchTerm) params.search = searchTerm;
      
      const data = await getAds(params);
      setAds(data);
    } catch (error) {
      console.error('Error loading ads:', error);
    }
  };

  const handleDeleteAd = async (adId) => {
    if (window.confirm('Are you sure you want to delete this ad?')) {
      try {
        await deleteAd(adId);
        setAds(ads.filter(ad => ad._id !== adId));
        setSelectedAd(null);
        await loadCategories(); // Refresh category counts
      } catch (error) {
        alert('Error deleting ad');
      }
    }
  };

  const handleUploadSuccess = () => {
    loadData();
    setShowUploadForm(false);
  };

  const handleCategoryCreate = async (categoryData) => {
    try {
      await createCategory(categoryData);
      await loadCategories();
    } catch (error) {
      console.error('Category creation failed:', error);
      throw error;
    }
  };

  const handleCategoryDelete = async (categoryId) => {
    try {
      await deleteCategory(categoryId);
      if (selectedCategory === categoryId) {
        setSelectedCategory('');
      }
      await loadCategories();
    } catch (error) {
      console.error('Category deletion failed:', error);
      throw error;
    }
  };

  const handleCategorySelect = (categoryId) => {
    setSelectedCategory(categoryId);
    setSearchTerm(''); // Clear search when switching categories
  };

  const getCurrentCategoryName = () => {
    if (!selectedCategory) return null;
    const category = categories.find(cat => cat._id === selectedCategory);
    return category ? category.name : null;
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="app">
      <Sidebar 
        categories={categories}
        selectedCategory={selectedCategory}
        onCategorySelect={handleCategorySelect}
        onNewUpload={() => setShowUploadForm(true)}
        onCategoryCreate={handleCategoryCreate}
        onCategoryDelete={handleCategoryDelete}
      />
      
      <main className="main-content">
        <header className="header">
          <div className="header-content">
            {selectedCategory && (
              <button 
                className="back-btn"
                onClick={() => setSelectedCategory('')}
              >
                ‚Üê Back to Categories
              </button>
            )}
            <h1>
              {selectedCategory ? getCurrentCategoryName() : 'Paper Ads Manager'}
            </h1>
          </div>
          <div className="search-container">
            <input
              type="text"
              placeholder={selectedCategory ? "Search in category..." : "Search categories..."}
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>
        </header>

        {error && <div className="error-message">{error}</div>}

        <div className="content-area">
          {viewMode === 'categories' ? (
            // Show category folders
            <div className="categories-grid">
              {categories.length > 0 ? (
                categories
                  .filter(category => 
                    !searchTerm || 
                    category.name.toLowerCase().includes(searchTerm.toLowerCase())
                  )
                  .map(category => (
                    <CategoryCard
                      key={category._id}
                      category={category}
                      onClick={() => handleCategorySelect(category._id)}
                      onDelete={() => handleCategoryDelete(category._id)}
                    />
                  ))
              ) : (
                <div className="empty-state">
                  <h3>No categories found</h3>
                  <p>Create your first category to organize your ads.</p>
                  <button 
                    className="btn btn-primary"
                    onClick={() => setShowUploadForm(true)}
                  >
                    Upload First Ad
                  </button>
                </div>
              )}
            </div>
          ) : (
            // Show ads in selected category
            <div className="ads-grid">
              {ads.length > 0 ? (
                ads.map(ad => (
                  <AdCard
                    key={ad._id}
                    ad={ad}
                    onView={() => setSelectedAd(ad)}
                    onDelete={() => handleDeleteAd(ad._id)}
                  />
                ))
              ) : (
                <div className="empty-state">
                  <h3>No ads in this category</h3>
                  <p>Upload some ads to this category to get started.</p>
                  <button 
                    className="btn btn-primary"
                    onClick={() => setShowUploadForm(true)}
                  >
                    Upload Ad
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </main>

      {selectedAd && (
        <AdModal
          ad={selectedAd}
          onClose={() => setSelectedAd(null)}
          onDelete={() => handleDeleteAd(selectedAd._id)}
        />
      )}

      {showUploadForm && (
        <UploadForm
          categories={categories}
          onClose={() => setShowUploadForm(false)}
          onSuccess={handleUploadSuccess}
        />
      )}
    </div>
  );
}

export default App;
