import React, { useState } from 'react';

const Sidebar = ({ categories, selectedCategory, onCategorySelect, onNewUpload, onCategoryCreate }) => {
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [isCreating, setIsCreating] = useState(false);
  const [error, setError] = useState('');

  const handleCreateCategory = async (e) => {
    e.preventDefault();
    if (!newCategoryName.trim()) {
      setError('Category name is required');
      return;
    }

    setIsCreating(true);
    setError('');

    try {
      // FIX: Pass as object, not a string
      await onCategoryCreate({ name: newCategoryName.trim() });
      setNewCategoryName('');
      setShowCreateForm(false);
    } catch (error) {
      setError('Failed to create category');
    } finally {
      setIsCreating(false);
    }
  };

  const cancelCreate = () => {
    setShowCreateForm(false);
    setNewCategoryName('');
    setError('');
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2>Categories</h2>
      </div>
      
      <button className="upload-btn" onClick={onNewUpload}>
        + Upload New Ad
      </button>

      <div className="category-section">
        <div className="category-header">
          <span>Browse Categories</span>
          <button 
            className="create-category-btn"
            onClick={() => setShowCreateForm(!showCreateForm)}
            title="Create New Category"
          >
            +
          </button>
        </div>

        {showCreateForm && (
          <form onSubmit={handleCreateCategory} className="create-category-form">
            <input
              type="text"
              value={newCategoryName}
              onChange={(e) => setNewCategoryName(e.target.value)}
              placeholder="Enter category name"
              className="category-input"
              disabled={isCreating}
              autoFocus
            />
            <div className="form-buttons">
              <button 
                type="submit" 
                className="confirm-btn"
                disabled={isCreating || !newCategoryName.trim()}
              >
                {isCreating ? 'Creating...' : 'Create'}
              </button>
              <button 
                type="button" 
                className="cancel-btn"
                onClick={cancelCreate}
                disabled={isCreating}
              >
                Cancel
              </button>
            </div>
            {error && <div className="error-message">{error}</div>}
          </form>
        )}

        <div className="categories-list">
          <button
            className={`category-item ${!selectedCategory ? 'active' : ''}`}
            onClick={() => onCategorySelect('')}
          >
            <span className="category-name">All Categories</span>
            <span className="category-count">
              {categories.reduce((total, cat) => total + (cat.count || 0), 0)}
            </span>
          </button>

          {categories.map((category) => (
            <button
              key={category._id}
              className={`category-item ${selectedCategory === category._id ? 'active' : ''}`}
              onClick={() => onCategorySelect(category._id)}
            >
              <span className="category-name">{category.name}</span>
              <span className="category-count">{category.count || 0}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
